.result:hover,.result.active{background:rgba(59,130,246,0.08);border-color:rgba(59,130,246,0.3)}
.result .title{font-weight:600;font-size:0.95rem;margin-bottom:4px}
.result .desc{color:var(--muted);font-size:0.85rem;line-height:1.5}
.result mark{background:rgba(59,130,246,0.25);color:var(--text);border-radius:2px;padding:0 2px}
