```html:index.html
<!DOCTYPE html>
<html lang="en">
<head>
<meta charset="UTF-8">
<meta name="viewport" content="width=device-width, initial-scale=1.0">
<title>Launch Countdown</title>
<link href="https://fonts.googleapis.com/css2?family=Bebas+Neue&family=Source+Sans+3:wght@300;400;600&display=swap" rel="stylesheet">
<style>
:root{--bg:#0a0a12;--surface:rgba(255,255,255,0.05);--border:rgba(255,255,255,0.08);--text:#f0f0f5;--muted:#888;--primary:#f43f5e;--font-display:'Bebas Neue',sans-serif;--font-body:'Source Sans 3',sans-serif}
*{margin:0;padding:0;box-sizing:border-box}
body{background:var(--bg);color:var(--text);font-family:var(--font-body);min-height:100vh;display:grid;place-items:center;position:relative;overflow:hidden}
body::before{content:'';position:absolute;top:-50%;left:-50%;width:200%;height:200%;background:radial-gradient(ellipse at 40% 40%,rgba(244,63,94,0.08),transparent 50%),radial-gradient(ellipse at 60% 60%,rgba(99,102,241,0.06),transparent 50%);animation:bg 10s ease-in-out infinite alternate}
@keyframes bg{to{transform:translate(5%,-3%)}}
.content{position:relative;z-index:1;text-align:center;padding:24px}
.eyebrow{font-size:0.82rem;text-transform:uppercase;letter-spacing:0.15em;color:var(--primary);font-weight:600;margin-bottom:16px}
h1{font-family:var(--font-display);font-size:3.5rem;letter-spacing:0.04em;margin-bottom:12px}
.desc{color:var(--muted);font-size:1.05rem;max-width:440px;margin:0 auto 48px;line-height:1.6}
.timer{display:flex;justify-content:center;gap:20px;margin-bottom:48px}
.unit{display:flex;flex-direction:column;align-items:center;gap:8px}
.digit{font-family:var(--font-display);font-size:5rem;letter-spacing:0.02em;background:var(--surface);border:1px solid var(--border);border-radius:14px;padding:12px 28px;line-height:1;min-width:110px}
.label{font-size:0.75rem;text-transform:uppercase;letter-spacing:0.1em;color:var(--muted);font-weight:600}
.notify{display:flex;gap:12px;justify-content:center;max-width:400px;margin:0 auto}
.notify input{flex:1;padding:14px 18px;background:var(--surface);border:1px solid var(--border);border-radius:10px;color:var(--text);font-family:var(--font-body);font-size:0.95rem;outline:none}
.notify input:focus{border-color:var(--primary)}
.notify input::placeholder{color:var(--muted)}
.notify button{padding:14px 24px;background:var(--primary);border:none;border-radius:10px;color:#fff;font-family:var(--font-body);font-weight:600;cursor:pointer;transition:all 0.2s ease;white-space:nowrap}
.notify button:hover{background:#e11d48;transform:translateY(-1px)}
@media(max-width:600px){h1{font-size:2.2rem}.digit{font-size:3rem;padding:10px 18px;min-width:72px}.timer{gap:10px}.notify{flex-direction:column}}
