nav{display:flex;justify-content:space-between;align-items:center;padding:16px 32px;position:fixed;top:0;left:0;right:0;z-index:100;background:rgba(11,11,16,0.85);backdrop-filter:blur(16px);border-bottom:1px solid var(--border)}
.logo{font-size:1.2rem;font-weight:700;letter-spacing:-0.02em}
.nav-links{display:flex;align-items:center;gap:32px;list-style:none}
