<button class="btn btn-success" onclick="toast('success','Saved!','Your changes have been saved successfully.')">Success</button>
<button class="btn btn-error" onclick="toast('error','Error','Something went wrong. Please try again.')">Error</button>
<button class="btn btn-info" onclick="toast('info','Info','A new version is available for download.')">Info</button>
<button class="btn btn-warning" onclick="toast('warning','Warning','Your trial expires in 3 days.')">Warning</button>
<div class="toast-container" id="toasts"></div>
<script>
const icons = { success:'fa-check', error:'fa-times', info:'fa-info', warning:'fa-exclamation' };
const durations = { success:3000, error:5000, info:4000, warning:4000 };

function toast(type, title, message) {
  const container = document.getElementById('toasts');
  const el = document.createElement('div');
  el.className = `toast ${type}`;
  const dur = durations[type];
  el.innerHTML = `
    <div class="icon"><i class="fas ${icons[type]}"></i></div>
    <div class="content"><div class="title">${title}</div><div class="message">${message}</div></div>
    <button class="close" onclick="dismiss(this.parentElement)">&times;</button>
    <div class="progress" style="animation-duration:${dur}ms"></div>`;
  container.appendChild(el);
  setTimeout(() => dismiss(el), dur);
}

function dismiss(el) {
  if (el.classList.contains('removing')) return;
  el.classList.add('removing');
  setTimeout(() => el.remove(), 300);
}
</script>
</body>
