    <div class="btns"><button class="btn btn-next" onclick="next(0)">Continue</button></div>
  </div>
  <div class="step" id="step-1">
    <h2>Company Details</h2><p class="desc">Tell us about your organization.</p>
    <div class="field" id="f-company"><label>Company Name</label><input id="company" placeholder="Acme Inc."><div class="err-msg">Company name required</div></div>
    <div class="field" id="f-role"><label>Your Role</label><select id="role"><option value="">Select a role</option><option>Engineering</option><option>Design</option><option>Product</option><option>Marketing</option><option>Other</option></select><div class="err-msg">Please select a role</div></div>
    <div class="btns"><button class="btn btn-back" onclick="back(1)">Back</button><button class="btn btn-next" onclick="next(1)">Continue</button></div>
  </div>
  <div class="step" id="step-2">
    <h2>Almost Done</h2><p class="desc">Any specific requirements?</p>
    <div class="field"><label>Message (optional)</label><textarea rows="4" placeholder="Tell us what you need..."></textarea></div>
    <div class="btns"><button class="btn btn-back" onclick="back(2)">Back</button><button class="btn btn-next" onclick="submit()">Submit</button></div>
  </div>
