<script>
const emailRe=/^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const dots=document.querySelectorAll('.dot');
function show(i){document.querySelectorAll('.step').forEach(s=>s.classList.remove('active'));document.getElementById('step-'+i).classList.add('active');dots.forEach((d,j)=>{d.classList.toggle('active',j===i);d.classList.toggle('done',j<i)})}
function vf(id,ok){document.getElementById('f-'+id)?.classList.toggle('error',!ok);return ok}
function next(step){
  if(step===0){const n=vf('name',document.getElementById('name').value.trim().length>0);const e=vf('email',emailRe.test(document.getElementById('email').value));if(n&&e)show(1)}
  if(step===1){const c=vf('company',document.getElementById('company').value.trim().length>0);const r=vf('role',document.getElementById('role').value!=='');if(c&&r)show(2)}
}
function back(step){show(step-1)}
function submit(){document.querySelectorAll('.step').forEach(s=>s.classList.remove('active'));document.getElementById('progress').style.display='none';document.getElementById('success').style.display='block'}
</script>
