<title>Search</title>
<link href="https://fonts.googleapis.com/css2?family=Sora:wght@300;400;500;600;700&display=swap" rel="stylesheet">
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.0/css/all.min.css">
<style>
:root { --bg:#0c0c11; --surface:rgba(255,255,255,0.04); --border:rgba(255,255,255,0.07); --text:#eee; --muted:#777; --primary:#3b82f6; --font:'Sora',sans-serif; }
*{margin:0;padding:0;box-sizing:border-box}
body{background:var(--bg);color:var(--text);font-family:var(--font);min-height:100vh;display:flex;justify-content:center;padding:80px 20px}
.container{width:100%;max-width:640px}
h1{font-size:1.8rem;font-weight:700;margin-bottom:8px;letter-spacing:-0.02em}
.sub{color:var(--muted);margin-bottom:32px;font-size:0.95rem}
.search-box{position:relative;margin-bottom:24px}
.search-box i{position:absolute;left:18px;top:50%;transform:translateY(-50%);color:var(--muted);font-size:0.9rem}
.search-box input{width:100%;padding:16px 18px 16px 46px;background:var(--surface);border:1px solid var(--border);border-radius:14px;color:var(--text);font-size:1rem;font-family:var(--font);outline:none;transition:border-color 0.2s ease}
.search-box input:focus{border-color:var(--primary)}
.search-box input::placeholder{color:var(--muted)}
.search-box .spinner{position:absolute;right:18px;top:50%;transform:translateY(-50%);display:none}
.search-box .spinner.show{display:block}
.search-box .spinner::after{content:'';display:block;width:18px;height:18px;border:2px solid var(--border);border-top-color:var(--primary);border-radius:50%;animation:spin 0.6s linear infinite}
@keyframes spin{to{transform:rotate(360deg)}}
.meta{display:flex;justify-content:space-between;color:var(--muted);font-size:0.8rem;margin-bottom:16px;padding:0 4px}
.results{display:flex;flex-direction:column;gap:8px}
.result{padding:18px 20px;background:var(--surface);border:1px solid var(--border);border-radius:12px;cursor:pointer;transition:all 0.25s ease;opacity:0;transform:translateY(8px);animation:fadeIn 0.3s ease forwards}
.result:hover,.result.active{background:rgba(59,130,246,0.08);border-color:rgba(59,130,246,0.3)}
.result .title{font-weight:600;font-size:0.95rem;margin-bottom:4px}
.result .desc{color:var(--muted);font-size:0.85rem;line-height:1.5}
.result mark{background:rgba(59,130,246,0.25);color:var(--text);border-radius:2px;padding:0 2px}
.empty{text-align:center;padding:48px 0;color:var(--muted);font-size:0.9rem}
