<style>
:root{--bg:#0e0e14;--surface:rgba(255,255,255,0.06);--border:rgba(255,255,255,0.08);--text:#f0f0f5;--muted:#888;--font:'DM Sans',sans-serif}
*{margin:0;padding:0;box-sizing:border-box}
body{background:var(--bg);color:var(--text);font-family:var(--font);min-height:100vh;display:flex;justify-content:center;align-items:center;gap:12px}
.btn{padding:12px 24px;border-radius:10px;border:none;font-family:var(--font);font-weight:600;font-size:0.88rem;cursor:pointer;transition:all 0.2s ease}
