  /* Navigation */
  nav { position: fixed; top: 0; width: 100%; z-index: 100; padding: 16px 24px; background: rgba(255,255,255,0.85); backdrop-filter: blur(20px); -webkit-backdrop-filter: blur(20px); border-bottom: 1px solid var(--border); }
