    .features-grid, .pricing-grid, .testimonials-grid { grid-template-columns: 1fr; }
    footer { flex-direction: column; }
  }
  @media (prefers-reduced-motion: reduce) { .fade-in { opacity: 1; transform: none; transition: none; } }
</style>
