  * { margin: 0; padding: 0; box-sizing: border-box; }
  body { font-family: var(--font-body); background: var(--bg); color: var(--text); line-height: 1.6; overflow-x: hidden; }
  a { text-decoration: none; color: inherit; }

  /* Navigation */
  nav { position: fixed; top: 0; width: 100%; z-index: 100; padding: 16px 24px; background: rgba(10,10,15,0.8); backdrop-filter: blur(20px); -webkit-backdrop-filter: blur(20px); border-bottom: 1px solid var(--border); }
  .nav-inner { max-width: 1200px; margin: 0 auto; display: flex; align-items: center; justify-content: space-between; }
  .logo { font-family: var(--font-display); font-size: 22px; font-weight: 700; background: linear-gradient(135deg, var(--primary), var(--primary-hover)); -webkit-background-clip: text; -webkit-text-fill-color: transparent; }
  .nav-links { display: flex; gap: 32px; align-items: center; }
  .nav-links a { font-size: 14px; color: var(--muted); transition: color 0.2s; }
  .nav-links a:hover { color: var(--text); }
  .btn { padding: 10px 24px; border-radius: 8px; font-size: 14px; font-weight: 600; cursor: pointer; transition: all 0.2s; border: none; font-family: var(--font-body); }
  .btn-primary { background: var(--primary); color: white; }
  .btn-primary:hover { background: var(--primary-hover); transform: translateY(-1px); box-shadow: 0 0 20px var(--primary-glow); }
  .btn-ghost { background: transparent; color: var(--text); border: 1px solid var(--border); }
  .btn-ghost:hover { border-color: var(--border-hover); background: var(--surface); }

  /* Hero */
  .hero { padding: 160px 24px 80px; max-width: 1200px; margin: 0 auto;  position: relative; }
  .hero::before { content: ''; position: absolute; top: 0; left: 50%; transform: translateX(-50%); width: 600px; height: 600px; background: radial-gradient(circle, var(--primary-subtle), transparent 70%); pointer-events: none; }
  .hero-badge { display: inline-flex; align-items: center; gap: 8px; padding: 6px 16px; background: var(--primary-subtle); border: 1px solid rgba(255,255,255,0.08); border-radius: 100px; font-size: 13px; color: var(--primary-hover); margin-bottom: 24px; }
  .hero h1 { font-family: var(--font-display); font-size: clamp(36px, 6vw, 64px); font-weight: 700; line-height: 1.1; letter-spacing: -0.02em; max-width: 600px; margin-bottom: 20px; }
  .hero h1 span { background: linear-gradient(135deg, var(--primary), var(--primary-hover)); -webkit-background-clip: text; -webkit-text-fill-color: transparent; }
  .hero p { font-size: 18px; color: var(--muted); max-width: 480px; margin-bottom: 40px; }
  .hero-cta { display: flex; gap: 12px;  flex-wrap: wrap; }


