*{margin:0;padding:0;box-sizing:border-box}body{font-family:var(--font-body);background:var(--bg);color:var(--text);line-height:1.6}
.container{max-width:1120px;margin:0 auto;padding:0 24px}
nav{padding:14px 0;border-bottom:1px solid var(--border);position:sticky;top:0;z-index:50;background:rgba(10,10,15,0.8);backdrop-filter:blur(20px)}
nav .container{display:flex;align-items:center;justify-content:space-between}
.logo{font-family:var(--font-display);font-size:20px;font-weight:700;background:linear-gradient(135deg,var(--primary),var(--primary-hover));-webkit-background-clip:text;-webkit-text-fill-color:transparent}
.nav-links{display:flex;gap:28px;align-items:center}
.nav-links a{font-size:14px;color:var(--muted);text-decoration:none;transition:color 0.2s}.nav-links a:hover{color:var(--text)}
.btn{padding:10px 24px;border-radius:8px;font-size:14px;font-weight:600;cursor:pointer;text-decoration:none;font-family:var(--font-body);transition:all 0.2s;border:none;display:inline-flex;align-items:center}
.btn-primary{background:var(--primary);color:#fff}.btn-primary:hover{background:var(--primary-hover);transform:translateY(-1px)}
.btn-ghost{background:transparent;color:var(--text);border:1px solid var(--border)}.btn-ghost:hover{border-color:var(--muted)}
.hero{padding:80px 0 60px;text-align:center}
.hero h1{font-family:var(--font-display);font-size:52px;font-weight:700;line-height:1.1;letter-spacing:-0.03em;margin-bottom:16px}
.hero p{font-size:18px;color:var(--muted);max-width:540px;margin:0 auto 32px}
.hero-btns{display:flex;gap:12px;justify-content:center}
.features{padding:80px 0}.section-title{font-family:var(--font-display);font-size:32px;font-weight:700;text-align:center;margin-bottom:48px}
.features-grid{display:grid;grid-template-columns:repeat(3,1fr);gap:16px}
.fcard{padding:28px;background:var(--surface);border:1px solid var(--border);border-radius:12px;transition:all 0.3s}
.fcard:hover{transform:translateY(-4px);border-color:rgba(255,255,255,0.12)}
.fcard h3{font-family:var(--font-display);font-size:17px;font-weight:600;margin:12px 0 6px}
.fcard p{font-size:14px;color:var(--muted);line-height:1.6}
.fcard .icon{font-size:28px}
footer{padding:24px 0;border-top:1px solid var(--border);text-align:center;font-size:13px;color:var(--muted)}
@media(max-width:768px){.hero h1{font-size:32px}.features-grid{grid-template-columns:1fr}.nav-links{gap:16px}}
</style></head><body>
